import React from 'react';

const Form = (props) =>{

    return(
        <form onSubmit={props.getweatherInfo}>
            <div className="control">
                <input type ="text" name="name" onChange={e => props.setCity(e.target.value)} placeholder="Enter the city to check weather"/>
            </div><br></br>
            <input type ="submit" value="Check Weather"/>
        </form>
    )
}

export default Form;
