import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {fetchWeather} from './actions/fetchWeather';

function App() {

  //set city
  const [city, setCity] = useState("")

  const weatherSelector = useSelector((state)=> state.weatherInfo)
  const dispatch = useDispatch();
  const getweatherInfoAction = (city) => dispatch(fetchWeather(city))

  useEffect(() => {
      getweatherInfoAction("mohali")
  },[])

  const getweatherInfo = (e) =>{
    e.preventDefault();
    if(city === ""){
      console.log("No city to search")
    }
    else{
      getweatherInfoAction(city)
      console.log("weather",weatherSelector)
    }
  }

  let details = "";
  if(weatherSelector){
    details = <div className = "deatils" >
      <h1>Weather Details</h1>
      <p>
        {weatherSelector.name}
          <span >
          {weatherSelector.sys.country}
        </span>
      </p>
      <p>{weatherSelector.main.temp}Â°F</p>
      <img src={weatherSelector.weather[0].icon}/>
    </div>
  }else{
    details = <p>You need to type a city</p>
  }

  return (
    <div className="App" >
      <h1>Wheather-App</h1>
      <form onSubmit={getweatherInfo}>
        <div className="control">
          <input type ="text" name="name" onChange={e => setCity(e.target.value)} placeholder="Enter the city to check weather"/>
        </div><br></br>
        <input type ="submit" value="Check Weather"/>
      </form>
      {details}
    </div>
  );
}

export default App;
