import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {fetchWeather} from './actions/fetchWeather';
import  Form from './components/Form'

function App() {
  const dispatch = useDispatch();

  //set city
  const [city, setCity] = useState("")
  // const [weatherData ,setWeatherData] =useState()

  const weatherSelector = useSelector((state)=> state.weatherInfo)
  const getweatherInfoAction = (city) => dispatch(fetchWeather(city))

  useEffect(() => {
      getweatherInfoAction(city)
  },[city])

  const getweatherInfo = (e) =>{
    e.preventDefault();
    if(city === ""){
      console.log("No city to search")
    }
    else{
      getweatherInfoAction(city)
      console.log("weatherSelector",weatherSelector)
    }
  }

  let details = "";
  if(weatherSelector){
    details = <div className = "deatils" >
      <h1>Weather Details</h1>
      <p>
        {weatherSelector}
          <span >
          {weatherSelector}
        </span>
      </p>
      <p>{weatherSelector}Â°F</p>
      <img src={weatherSelector}/>
      <p>{weatherSelector}</p>
    </div>
  }else{
    details = <p>You need to type a city</p>
  }

  return (
    <div className="App" >
      <Form/>
      {details}
    </div>
  );
}

export default App;
