import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {fetchWeather} from './actions/fetchWeather';
import  Form from './components/Form'

function App() {
  const dispatch = useDispatch();

  //set city
  const [city, setCity] = useState("")
  // const [weatherData ,setWeatherData] =useState()

  const weatherSelector = useSelector((state)=> state.weatherInfo)
  const getweatherInfoAction = (city) => dispatch(fetchWeather(city))

  useEffect(() => {
      getweatherInfoAction(city)
  },[city])

  const getweatherInfo = (e) =>{
    debugger
    e.preventDefault();
    if(city === ""){
      console.log("No city to search")
    }
    else{
      getweatherInfoAction(city)
      console.log("weatherSelector",weatherSelector)
    }
  }

  let details = "";
  if(weatherSelector){
    details = <div className = "deatils" >
      <h1>Weather Details</h1>
      <p>
        {weatherSelector.name}
          <span >
          {weatherSelector.sys}
        </span>
      </p>
      <p>{weatherSelector.main}Â°F</p>
      <img src={weatherSelector.weather[0].icon}/>
      <p>{weatherSelector.weather[0].main}</p>
    </div>
  }else{
    details = <p>You need to type a city</p>
  }

  return (
    <div className="App" >
      <h1>Wheather-App</h1>
      <form onSubmit={getweatherInfo}>
        <div className="control">
          <input type ="text" name="name" onChange={e => setCity(e.target.value)} placeholder="Enter the city to check weather"/>
        </div><br></br>
        <input type ="submit" value="Check Weather"/>
      </form>
      <Form/>
      {details}
    </div>
  );
}

export default App;
